import {useEffect, useState} from 'react'
import {Link} from 'react-router-dom'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHammer, faDragon, faFistRaised} from '@fortawesome/free-solid-svg-icons'

function Footer(){
    const [ page, setPage ] = useState()

    useEffect(() => {
        page = window.location.pathname
    },[])

    if (page === "/"){
        return (
            null
        )
    }
    else if (page === "/attacks"){
        return (
            <footer  className="flex text-indigo-500 justify-between bottom-0 fixed items-center w-full text-2xl p-1 pr-5 pl-5 bg-gray-100 border-t-2 md:text-3xl">
                <Link className="hover:text-indigo-400" to="/attack-builder"><FontAwesomeIcon icon={faHammer}/></Link>
            </footer>
        )
    }
    else if (page === "/creatures"){
        return (
            <footer  className="flex text-indigo-500 justify-between bottom-0 fixed items-center w-full text-2xl p-1 pr-5 pl-5 bg-gray-100 border-t-2 md:text-3xl">
                <Link className="hover:text-indigo-400" to="/attack-builder"><FontAwesomeIcon icon={faHammer}/></Link>
            </footer>
        )
    }

}

export default Footer
