import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShieldAlt, faHeart, faMeteor } from '@fortawesome/free-solid-svg-icons';
import React, { useState, useEffect }from 'react'

function AttackList(){
    const [ attacks, setAttacks ] = useState([])

    useEffect(() => {
        fetch("http://localhost:8080/all-attacks")
            .then(res => res.json())
            .then(
                (result) => {
                    setAttacks(result);
                }).catch()
  }, []);

    return (
        <table className="w-11/12 left-1/3 table text-xs lg:text-md">
            <thead>
                <th className="w-1/6 border-grey-400 border-b-2 border-r-2 border-l-2">Name</th>
                <th className="w-2/6 border-grey-400 border-b-2 border-r-2 border-l-2">Description</th>
                <th className="w-1/12 border-grey-400 border-b-2 border-r-2 border-l-2">Damage</th>
                <th className="w-1/6 border-grey-400 border-b-2 border-r-2 border-l-2">Damage Type</th>
                <th className="w-1/6 border-grey-400 border-b-2 border-r-2 border-l-2">Attack Type</th>
                <th className="w-1/12 border-grey-400 border-b-2 border-r-2 border-l-2">Area</th>
            </thead>
            <tbody>
                {attacks.sort(
                    function(first, second) {
                        return first.id - second.id;
                    }).map(
                    attack => (<tr><td className="border-grey-400 border-b-2 border-r-2 border-l-2 pl-2 pr-2">{attack.name}</td>
                            <td className="border-grey-400 border-b-2 border-r-2 border-l-2 pl-2 pr-2">{attack.description}</td>
                            <td className="border-grey-400 border-b-2 border-r-2 border-l-2 pl-2 pr-2">{attack.damage}</td>
                            <td className="border-grey-400 border-b-2 border-r-2 border-l-2 pl-2 pr-2">{attack.damageType}</td>
                            <td className="border-grey-400 border-b-2 border-r-2 border-l-2 pl-2 pr-2">{attack.attackType}</td>
                            <td className="border-grey-400 border-b-2 border-r-2 border-l-2 pl-2 pr-2">{attack.area}</td>
                            </tr>
                        )
                )}
            </tbody>
        </table>
    )
}

export default AttackList
