import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { sword } from '@fortawesome/free-solid-svg-icons'
import React, { useState, useEffect } from 'react'

function CreatureMaker(){
    const [ creatures, setCreatures ] = useState([])

    useEffect(() => {
        fetch("http://localhost:8080/all-creatures")
            .then(res => res.json())
            .then(
                (result) => {
                    setCreatures(result);
                }).catch()
  }, []);

    return (
        <div>
            <table className="table-auto w-full text-sm">
                <thead>
                    <th>Name</th>
                    <th>Type</th>
                    <th>Description</th>
                    <th>Attacks</th>
                    <th>Stats</th>
                </thead>
                <tbody>
                    {creatures.sort(
                        function(first, second) {
                            return first.id - second.id;
                        }).map(
                        creature => (<tr><td>{creature.name}</td>
                                <td>{creature.type}</td>
                                <td>{creature.description}</td>
                                <td>{
                                    creature.attacks.map(
                                        attack => (<p>{attack.name}</p>)
                                    )
                                }</td>
                                <td>{
                                    <div>
                                        <p>
                                            <FontAwesomeIcon icon="sword"/> {creature.stats.health}
                                        </p>
                                        <p>
                                            attack : {creature.stats.attack}
                                        </p>
                                        <p>
                                            defense : {creature.stats.defense}
                                        </p>
                                    </div>
                                }</td>
                                </tr>
                            )
                    )}
                </tbody>
            </table>
        </div>
    )
}

export default CreatureMaker
