import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShieldAlt, faHeart, faMeteor } from '@fortawesome/free-solid-svg-icons';
import React from 'react'

function CreatureList(creatures){
  const creaturesList = [].slice.call(creatures)
    return (
        <div>
            <table className="text-sm">
                <thead>
                    <th className="border-grey-400 border-b-2 border-r-2 border-l-2 pl-2 pr-2">Name</th>
                    <th className="border-grey-400 border-b-2 border-r-2 border-l-2 pl-2 pr-2">Type</th>
                    <th className="border-grey-400 border-b-2 border-r-2 border-l-2 pl-2 pr-2">Description</th>
                    <th className="border-grey-400 border-b-2 border-r-2 border-l-2 pl-2 pr-2">Attacks</th>
                    <th className="border-grey-400 border-b-2 border-r-2 border-l-2 pl-2 pr-2">Stats</th>
                </thead>
                <tbody>
                    {creaturesList.sort(
                        function(first, second) {
                            return first.id - second.id;
                        }).map(
                        creature => (<tr><td className="border-grey-400 border-b-2 border-r-2 border-l-2 pl-2 pr-2">{creature.name}</td>
                                <td className="border-grey-400 border-b-2 border-r-2 border-l-2 pl-2 pr-2">{creature.type}</td>
                                <td className="border-grey-400 border-b-2 border-r-2 border-l-2 pl-2 pr-2">{creature.description}</td>
                                <td className="border-grey-400 border-b-2 border-r-2 border-l-2 pl-2 pr-2">{
                                    creature.attacks.map(
                                        attack => (<p>{attack.name}</p>)
                                    )
                                }</td>
                                <td className="border-grey-400 border-b-2 border-r-2 border-l-2 pl-2 pr-2">{
                                    <div>
                                        <p>
                                            <FontAwesomeIcon icon={faHeart} className="text-green-700"/> 
                                            {creature.stats.health}
                                        </p>
                                        <p>
                                            <FontAwesomeIcon icon={faMeteor} className="text-red-700"/> 
                                            {creature.stats.attack}
                                        </p>
                                        <p>
                                            <FontAwesomeIcon icon={faShieldAlt} className="text-yellow-500"/> 
                                            {creature.stats.defense}
                                        </p>
                                    </div>
                                }</td>
                                </tr>
                            )
                    )}
                </tbody>
            </table>
        </div>
    )
}

export default CreatureList
