import {useState} from 'react'
import {Link} from 'react-router-dom'

function Navigation(){
    const [ open, setOpen ] = useState(false);

    const toggleOpen = () => {
        setOpen(!open);
    }

    if(open){
        return (
            <div className="m-auto text-center shadow-md bg-gray-300 rounded-b-full w-2/6 h-2" onClick={() => toggleOpen()}>
            </div>
        )
    }
    else{
        return (
            <div className="m-auto text-center shadow-md bg-gray-300 rounded-b-2xl w-2/6" onClick={() => toggleOpen()}>
            <p><Link to="/">Home</Link></p>
            <p><Link to="/about">About Me</Link></p>
            </div>
        )
    }
}

export default Navigation
