import React, { useState, useEffect } from 'react'

function CreatureBuilder(){

    const [ name , setName ] = useState(null)
    const [ type , setType ] = useState(null)
    const [ description , setDescription ] = useState(null)
    const [ attacks , setAttacks ] = useState([])
    const [ stats , setStats ] = useState({health:null, attack:null, defense:null})
    const [ attackList, setAttackList ] = useState([])
    const [ attacksInput, setAttacksInput ] = useState([])

    useEffect(() => {
        fetch("http://localhost:8080/all-attacks")
            .then(res => res.json())
            .then(
                (result) => {
                    setAttackList(result);
                }).catch()
  }, [])

    const onSubmit = () => {
        fetch("http://localhost:8080/creature", {
            method: "POST",
            body: JSON.stringify({
                    name:name,
                    type:type, 
                    description:description, 
                    attacks:attacks, 
                    stats:stats
                }),
            headers: {"Content-type": "application/json; charset=UTF-8"}
        }).then(async (data) => {
            if (data.ok) {
                data = await data.json()
                window.open("/creatures", "_self")
            }
            else {
                alert("There was a server error!")
            }
        }).catch(e => console.log('Connection error', e))
    }

    const addAttack = (e) => {
        fetch("http://localhost:8080/attack/"+e.target.value)
                .then(res => res.json())
                .then(
                    (result) => {
                        setAttacks([...attacks, result]);
                    }).catch()
    }

    const addAttackInput = () => {
        setAttacksInput([...attacksInput, 
            <select className="mb-10" onChange={addAttack}>
                <option value="Select an attack">- Select an attack -</option>
                {attackList.map((attack) => <option key={attack.id} value={attack.id}>{attack.name}</option>)}
            </select>
        ])
    }
    const removeAttackInput = () => {
        setAttacksInput(attacksInput.slice(0, -1))
        setAttacks(attacks.slice(0, -1))
    }

    return (
        <div>
            <form className="flex flex-col items-center space-y-10 p-10 bg-gray-100 border-2 border-gray-200 my-10">
                <input type="text" placeholder="Name" onChange={e => setName(e.target.value)}/>
                <input type="text" placeholder="Type" onChange={e => setType(e.target.value)}/>
                <input className="h-32"type="text" placeholder="Description" onChange={e => setDescription(e.target.value)}/>
                <p>Attacks:</p>
                <div className="flex flex-col items-center">
                    {/* Return the attack inputs*/}
                    {attacksInput}
                    <div onClick={removeAttackInput} className="cursor-pointer text-3xl">-</div>
                    <div onClick={addAttackInput} className="cursor-pointer text-3xl">+</div>
                </div>
                <p>Stats:</p>
                <input type="number" placeholder="Health" onChange={e => stats.health = e.target.value}/>
                <input type="number" placeholder="Attack" onChange={e => stats.attack = e.target.value}/>
                <input type="number" placeholder="Defense" onChange={e => stats.defense = e.target.value}/>
                <button onClick={onSubmit}>Submit</button>
            </form>
        </div>
    )
}

export default CreatureBuilder
