import React, { useState, useEffect } from 'react'

function CreatureMaker(){
    const [ creatures, setCreatures ] = useState([])

    useEffect(() => {
        fetch("http://localhost:8080/all-creatures")
            .then(res => res.json())
            .then(
                (result) => {
                    setCreatures(result);
                }).catch()
  }, []);

    return (
        <div>
            <table className="table-auto">
                <thead>
                    <th>Name</th>
                    <th>Type</th>
                    <th>Description</th>
                    <th>Attacks</th>
                    <th>Stats</th>
                </thead>
                <tbody>
                    {creatures.sort(
                        function(first, second) {
                            return first.id - second.id;
                        }).map(
                        creature => (<tr><td>{creature.name}</td>
                                <td>{creature.type}</td>
                                <td>{creature.description}</td>
                                <td>{
                                    creature.attacks.map(
                                        attack => (attack.name)
                                    )
                                }</td>
                                <td>{
                                    creature.stats.map(
                                        stat => (<p>
                                            health : {stat.health}
                                            attack : {stat.attack}
                                            defense : {stat.defense}
                                        </p>)
                                    )
                                }</td>
                                </tr>
                            )
                    )}
                </tbody>
            </table>
        </div>
    )
}

export default CreatureMaker
